import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./admin.css"; // Import CSS for styling

function Admin() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showTransactions, setShowTransactions] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const fetchTransactions = async () => {
    if (showTransactions) {
      // If transactions are already shown, hide them and return
      setShowTransactions(false);
      return;
    }

    setIsLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:3001/api/transactions", {
        method: "GET",
        headers: {
          Authorization: token, // Send token for authentication
        },
      });
      const data = await response.json();

      if (response.ok) {
        console.log(data);
        setTransactions(data.transactions);
        setShowTransactions(true); // Show transactions
      } else {
        console.error(data.error);
      }
    } catch (err) {
      console.error("Failed to fetch transactions:", err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="admin-container">
      <div className="dashboard-header">
        <h1 className="title">Admin Dashboard</h1>
        <div className="button-group">
          <button className="button primary-button" onClick={handleLogout}>
            Logout
          </button>
          <button
            className="button primary-button"
            onClick={fetchTransactions}
            disabled={isLoading}
          >
            {isLoading
              ? "Loading..."
              : showTransactions
              ? "Hide Transactions"
              : "View Transactions"}
          </button>
        </div>
      </div>
      <div className="transactions-table">
        {showTransactions && transactions.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Transaction ID</th>
                <th>Amount (USD)</th>
                <th>Sender</th>

                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction.transactionId}>
                  <td>{transaction.transactionId}</td>
                  <td>{transaction.amount}</td>
                  <td>{transaction.senderEmail}</td>

                  <td>{transaction.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          showTransactions && <p>No transactions found.</p>
        )}
      </div>
    </div>
  );
}

export default Admin;
