import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useStripe, useElements, CardElement } from "@stripe/react-stripe-js";
import "./payment.css";

function Payment() {
  const [amount, setAmount] = useState("");
  const [recipientEmail, setRecipientEmail] = useState("");
  const [recipientCountry, setRecipientCountry] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const countryOptions = [
    { code: "USD", name: "United States" },
    { code: "INR", name: "India" },
    { code: "GBP", name: "United Kingdom" },
  ];

  async function handlePayment(e) {
    e.preventDefault();

    if (!stripe || !elements) {
      setError("Stripe is not loaded yet.");
      return;
    }

    if (!amount || !recipientEmail || !recipientCountry) {
      setError("Please fill in all fields.");
      return;
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(recipientEmail)) {
      setError("Please enter a valid email.");
      return;
    }

    if (amount <= 0) {
      setError("Amount must be greater than zero.");
      return;
    }

    try {
      // Initiate payment and get client secret and transaction ID from the backend
      const senderCurrency = "USD";

      const response = await fetch("http://localhost:3001/api/pay", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: localStorage.getItem("token"), // JWT token
        },
        body: JSON.stringify({
          amount,
          recipientEmail,
          senderCurrency,
          recipientCountry,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(
          errorData.message ||
            "An error occurred while processing your payment. Please try again."
        );
        return;
      }

      const { clientSecret, transactionId, exchangeRate } =
        await response.json();
      const convertedAmount = amount * exchangeRate;

      // Confirm payment with Stripe
      const { paymentIntent, error: stripeError } =
        await stripe.confirmCardPayment(clientSecret, {
          payment_method: {
            card: elements.getElement(CardElement),
          },
          metadata: {
            recipientEmail,
            recipientCountry,
          },
        });

      if (stripeError) {
        setError(stripeError.message);
      } else if (paymentIntent.status === "succeeded") {
        // Update transaction status on the backend
        await fetch("http://localhost:3001/api/update-status", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: localStorage.getItem("token"),
          },
          body: JSON.stringify({
            transactionId,
            paymentIntentId: paymentIntent.id,
            convertedAmount,
          }),
        });

        setSuccess(true);
        setTimeout(() => {
          navigate("/dashboard"); // Redirect to dashboard or another page after success
        }, 2000);
      } else {
        setError("Payment was not successful. Please try again.");
      }
    } catch (err) {
      console.error(err);
      setError(
        "An error occurred while processing your payment. Please try again."
      );
    }
  }

  return (
    <div className="container">
      <h1>Make a Payment</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">Payment successful!</p>}
      <form onSubmit={handlePayment}>
        <div className="form-group">
          <label>Recipient Email</label>
          <input
            type="email"
            value={recipientEmail}
            onChange={(e) => setRecipientEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Amount (in USD)</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Recipient Country</label>
          <select
            value={recipientCountry}
            onChange={(e) => setRecipientCountry(e.target.value)}
            required
          >
            <option value="" disabled>
              Select Country
            </option>
            {countryOptions.map((country) => (
              <option key={country.code} value={country.name}>
                {country.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Card Details</label>
          <div className="card-element-container">
            <CardElement />
          </div>
        </div>

        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Pay Now
          </button>
        </div>
      </form>
    </div>
  );
}

export default Payment;
