import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useStripe, useElements, CardElement } from "@stripe/react-stripe-js";
import "./payment.css";

function Payout() {
  const [amount, setAmount] = useState("");
  const [recipientEmail, setRecipientEmail] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  async function handlePayment(e) {
    e.preventDefault();
    if (!stripe || !elements) {
      setError("Stripe is not loaded yet.");
      return;
    }

    try {
      // Initiate payment and get client secret and transaction ID from the backend

      const response = await fetch("http://localhost:3001/api/payout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: localStorage.getItem("token"), // JWT token
        },
        body: JSON.stringify({
          amount,

          recipientEmail,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(
          errorData.message ||
            "An error occurred while processing your payment. Please try again."
        );
        return;
      }
    } catch (err) {
      console.error("Error:", err);
      setError(
        "An error occurred while processing your payment. Please try again."
      );
    }
  }

  return (
    <div className="container">
      <h1>Make a Payout</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">Payment successful!</p>}
      <form onSubmit={handlePayment}>
        <div className="form-group">
          <label>Amount (in USD)</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Recipient Email</label>
          <div className="card-element-container">
            <input
              type="email"
              value={recipientEmail}
              onChange={(e) => setRecipientEmail(e.target.value)}
              required
            />
          </div>
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Pay Now
          </button>
        </div>
      </form>
    </div>
  );
}

export default Payout;
