import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useStripe, useElements, CardElement } from "@stripe/react-stripe-js";
import "./payment.css";

function Payment() {
  const [amount, setAmount] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  async function handlePayment(e) {
    e.preventDefault();
    if (!stripe || !elements) {
      setError("Stripe is not loaded yet.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/api/pay", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: localStorage.getItem("token"),
        },
        body: JSON.stringify({ amount }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setError(errorData.message || "Payment error. Please try again.");
        return;
      }

      const { clientSecret } = await response.json();
      const { paymentIntent, error: stripeError } =
        await stripe.confirmCardPayment(clientSecret, {
          payment_method: {
            card: elements.getElement(CardElement),
          },
        });

      if (stripeError) {
        setError("Payment failed. Please try again.");
      } else if (paymentIntent.status === "succeeded") {
        setSuccess(true);
        setTimeout(() => navigate("/dashboard"), 2000);
      } else {
        setError("Payment not successful. Try again.");
      }
    } catch (err) {
      setError("Error processing payment. Try again.");
    }
  }

  return (
    <div className="container">
      <h1>Make a Payment</h1>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success-message">Payment successful!</p>}
      <form onSubmit={handlePayment}>
        <div className="form-group">
          <label>Amount (in USD)</label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Card Details</label>
          <CardElement />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary">
            Pay Now
          </button>
        </div>
      </form>
    </div>
  );
}

export default Payment;
