import React, { useEffect, useState } from "react";
import jwt from "jsonwebtoken";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const [dashboard, setDashboard] = useState("");
  const [tempDashboard, setTempDashboard] = useState("");
  async function populateDashboard() {
    const req = await fetch("http://localhost:3001/api/dashboard", {
      headers: {
        "x-access-token": localStorage.getItem("token"),
      },
    });
    const data = req.json();
    if (data.status === "ok") {
      setDashboard(data.dashboard);
    } else {
      alert(data.console.error);
    }
    console.log(data);
  }

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      const user = jwt.decode(token);
      if (!user) {
        localStorage.removeItem("token");
        navigate("/login");
      } else {
        populateDashboard();
      }
    }
  }, [navigate]);

  return (
    <div>
      <h1>Dashboard:{dashboard || "Not Found"} </h1>;
      <input
        type="text"
        placeholder="dashboard"
        value={tempDashboard}
        setValue={(e) => setTempDashboard(e.target.value)}
      />
    </div>
  );
};
export default Dashboard;
