import React, { useEffect, useState } from "react";

const BASE_URL = "http://localhost:3001/api/rates"; // Replace with your backend API endpoint

function CurrencyConvert() {
  const [currencyOptions, setCurrencyOptions] = useState([]);
  const [fromCurrency, setFromCurrency] = useState("");
  const [toCurrency, setToCurrency] = useState("");
  const [amount, setAmount] = useState("");
  const [convertedAmount, setConvertedAmount] = useState("");

  useEffect(() => {
    fetch(BASE_URL)
      .then((res) => res.json())
      .then((data) => {
        setCurrencyOptions([data.base, ...Object.keys(data.rates)]);
        setFromCurrency(data.base);
        setToCurrency(Object.keys(data.rates)[0]);
      });
  }, []);

  const handleConversion = async () => {
    if (!amount || !fromCurrency || !toCurrency) return;

    try {
      const response = await fetch("http://localhost:3001/api/rates");
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      const rate = data.rates[toCurrency] / data.rates[fromCurrency];
      setConvertedAmount((amount * rate).toFixed(2));
    } catch (error) {
      console.error("Error fetching exchange rate:", error);
      setConvertedAmount("Error fetching exchange rate"); // Handle error state
    }
  };

  return (
    <div>
      <h2>Currency Converter</h2>
      <p>Convert currencies with ease.</p>
      <div>
        <input
          type="number"
          placeholder="Enter amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <select
          value={fromCurrency}
          onChange={(e) => setFromCurrency(e.target.value)}
        >
          {currencyOptions.map((option) => (
            <option key={option} value={option}>
              {option}
            </option>
          ))}
        </select>
        =
        <input
          type="number"
          placeholder="Changed amount"
          value={convertedAmount || ""}
          readOnly
        />
        <select
          value={toCurrency}
          onChange={(e) => setToCurrency(e.target.value)}
        >
          {currencyOptions.map((option) => (
            <option key={option} value={option}>
              {option}
            </option>
          ))}
        </select>
        <button onClick={handleConversion}>Convert</button>
      </div>
    </div>
  );
}

export default CurrencyConvert;
