import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Register from "./pages/Register";
import Login from "./pages/Login";
import Dashboard from "./pages/Dashboard";
import "./App.css"; // Custom CSS for background and additional styling

const Home = () => {
  return (
    <div className="home-container">
      <header className="hero-header text-center">
        <h1>Welcome to Premium Payment Solutions</h1>
        <p>Convert currencies and manage your payments effortlessly</p>
        <nav className="nav-buttons">
          <Link to="/register" className="btn btn-primary m-2">
            Register
          </Link>
          <Link to="/login" className="btn btn-secondary m-2">
            Login
          </Link>
        </nav>
      </header>
      <main className="text-center mt-4">
        <h2>Why Choose Our Payment Page?</h2>
        <p>
          Experience seamless currency conversion, secure transactions, and
          more.
        </p>
      </main>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
};

export default App;
