import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Admin() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const fetchTransactions = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:3001/api/transactions", {
        method: "GET",
        headers: {
          Authorization: token, // Send token for authentication
        },
      });
      const data = await response.json();

      if (response.ok) {
        setTransactions(data.transactions);
      } else {
        console.error(data.error);
      }
    } catch (err) {
      console.error("Failed to fetch transactions:", err);
    }
  };

  useEffect(() => {
    fetchTransactions();
  }, []);

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1 className="title">Admin Dashboard</h1>
        <button className="button primary-button" onClick={handleLogout}>
          Logout
        </button>
        <button className="button primary-button" onClick={fetchTransactions}>
          View Transactions
        </button>
      </div>
      <div className="transactions-list">
        <h2>Transaction History</h2>
        <ul>
          {transactions.map((transaction) => (
            <li key={transaction.transactionId}>
              <p>Amount: {transaction.amount}</p>
              <p>Sender: {transaction.senderEmail}</p>
              <p>Recipient: {transaction.recipientEmail}</p>
              <p>Status: {transaction.status}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Admin;
