import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Payment() {
  const [amount, setAmount] = useState("");
  const [recipientEmail, setRecipientEmail] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  async function makePayment(e) {
    e.preventDefault();

    if (!amount || !recipientEmail) {
      setError("Please fill all fields");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:3001/api/pay", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: token, // Send token for authentication
        },
        body: JSON.stringify({ amount, recipientEmail }),
      });

      const data = await response.json();

      if (data.clientSecret) {
        // Redirect to Stripe Checkout with client secret
        window.location.href = `https://checkout.stripe.com/pay/${data.clientSecret}`;
      } else {
        setError(data.error || "Failed to make payment");
      }
    } catch (err) {
      setError(
        "An error occurred while processing your payment. Please try again."
      );
    }
  }

  return (
    <div className="container">
      <h1 className="title">Make a Payment</h1>
      {error && <p className="error-message">{error}</p>}
      <form onSubmit={makePayment} className="form">
        <div className="form-group">
          <label>Amount</label>
          <input
            className="input"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            type="number"
            placeholder="Enter amount in USD"
            required
          />
        </div>
        <div className="form-group">
          <label>Recipient Email</label>
          <input
            className="input"
            value={recipientEmail}
            onChange={(e) => setRecipientEmail(e.target.value)}
            type="email"
            placeholder="Enter recipient's email"
            required
          />
        </div>
        <div className="form-group">
          <input className="btn btn-login" type="submit" value="Pay" />
        </div>
      </form>
    </div>
  );
}

export default Payment;
