import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";

function Dashboard() {
  const [amount, setAmount] = useState("");
  const [recipient1, setRecipient1] = useState("");
  const [recipient2, setRecipient2] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Fetch the logged-in user's email from the location state or other storage method
    const userEmail =
      location.state?.email || localStorage.getItem("userEmail");

    if (userEmail) {
      setRecipient1(userEmail);
    }
  }, [location]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userEmail");
    navigate("/");
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    setPaymentStatus("Processing...");

    if (!amount || !recipient2) {
      setPaymentStatus("Please fill in both recipient email fields.");
      return;
    }

    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    if (!emailPattern.test(recipient1) || !emailPattern.test(recipient2)) {
      setPaymentStatus("Invalid email format.");
      return;
    }

    try {
      const response = await fetch("http://localhost:4001/api/make-payment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          amount,
          recipients: [recipient1, recipient2],
        }),
      });

      const data = await response.json();

      if (data.success) {
        setPaymentStatus("Payment Successful!");
      } else {
        setPaymentStatus("Payment Failed. " + data.error);
      }
    } catch (error) {
      setPaymentStatus("An error occurred. Please try again.");
    }
  };

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1 className="title">User Dashboard</h1>
        <button className="button primary-button" onClick={handleLogout}>
          Logout
        </button>
        <h2 className="subtitle">Make a Payment</h2>
        <form onSubmit={handlePayment} className="form">
          <div className="form-group">
            <label>Amount (USD)</label>
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="input"
              required
            />
          </div>

          <div className="form-group">
            <label>Recipient 1 Email</label>
            <input type="email" value={recipient1} className="input" disabled />
          </div>

          <div className="form-group">
            <label>Recipient 2 Email</label>
            <input
              type="email"
              value={recipient2}
              onChange={(e) => setRecipient2(e.target.value)}
              className="input"
              required
            />
          </div>

          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Send Payment
            </button>
          </div>
        </form>

        {paymentStatus && <p>{paymentStatus}</p>}
      </div>
    </div>
  );
}

export default Dashboard;
