import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import List from "../components/List"; // Import the List component

function Dashboard() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalSpent, setTotalSpent] = useState(0);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(
          "http://localhost:3001/api/user-transactions",
          {
            method: "GET",
            headers: { Authorization: token },
          }
        );

        if (!response.ok) {
          throw new Error("Failed to fetch transactions");
        }

        const data = await response.json();
        setTransactions(data.transactions);

        const total = data.transactions.reduce(
          (sum, transaction) => sum + transaction.amount,
          0
        );
        setTotalSpent(total);

        setLoading(false);
      } catch (err) {
        console.error("Error fetching user transactions:", err);
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const goToPaymentPage = () => {
    navigate("/pay");
  };

  const goToPayoutPage = () => {
    navigate("/payout");
  };

  return (
    <div className="container">
      <header className="dashboard-header">
        <h1 className="dashboard-title">User Dashboard</h1>
        <div className="dashboard-actions">
          <button className="button primary-button" onClick={handleLogout}>
            Logout
          </button>
          <button className="button primary-button" onClick={goToPaymentPage}>
            Go to Pay
          </button>
          <button className="button primary-button" onClick={goToPayoutPage}>
            Go to Payout
          </button>
        </div>
      </header>
      <section>
        {loading ? (
          <p>Loading your transactions...</p>
        ) : (
          <List
            transactions={transactions}
            headers={["Date", "Amount", "Status/Receipt"]}
            noDataMessage="No transactions found."
            currency={transactions[0]?.currency}
            showTotal={true}
            totalSpent={totalSpent}
          />
        )}
      </section>
    </div>
  );
}

export default Dashboard;
