import React from "react";

function List({
  transactions,
  headers,
  noDataMessage,
  showTotal,
  totalSpent,
  currency,
}) {
  return (
    <div className="transactions-section">
      <h2>Your Transactions</h2>
      {transactions.length > 0 ? (
        <>
          {showTotal && (
            <p className="total-spent">
              <strong>Total Amount Spent:</strong> {totalSpent.toFixed(2)}{" "}
              {currency}
            </p>
          )}
          <table className="transactions-table">
            <thead>
              <tr>
                {headers.map((header) => (
                  <th key={header}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction._id || transaction.transactionId}>
                  {headers.map((header) => {
                    const fieldValue =
                      transaction[header.toLowerCase().replace(/\s+/g, "")];
                    return (
                      <td key={header}>
                        {typeof fieldValue === "string" &&
                        fieldValue.startsWith("http") ? (
                          <a
                            href={fieldValue}
                            target="_blank"
                            rel="noopener noreferrer"
                            style={{
                              color: "blue",
                              textDecoration: "underline",
                            }}
                          >
                            View Receipt
                          </a>
                        ) : (
                          fieldValue || "N/A"
                        )}
                      </td>
                    );
                  })}
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p>{noDataMessage}</p>
      )}
    </div>
  );
}

export default List;
