// Payout.js
import React, { useState } from "react";

const Payout = () => {
  const [amount, setAmount] = useState("");
  const [currency, setCurrency] = useState("usd"); // Assuming USD as default

  const handlePayout = async () => {
    try {
      const response = await fetch("/api/create-payout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: localStorage.getItem("token"),
        },
        body: JSON.stringify({ amount, currency }),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      if (data.status === "ok") {
        alert("Payout initiated successfully!");
      } else {
        alert("Payout failed: " + data.error);
      }
    } catch (err) {
      console.error("Error:", err);
      alert("An error occurred: " + err.message);
    }
  };

  return (
    <div>
      <h2>Initiate Payout</h2>
      <input
        type="number"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        placeholder="Enter amount"
      />
      <select value={currency} onChange={(e) => setCurrency(e.target.value)}>
        <option value="usd">USD</option>
        <option value="eur">EUR</option>
        <option value="gbp">GBP</option>
        {/* Add more currencies as needed */}
      </select>
      <button onClick={handlePayout}>Initiate Payout</button>
    </div>
  );
};

export default Payout;
