import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const token = localStorage.getItem("token"); // Assuming token is stored in localStorage
        const response = await fetch(
          "http://localhost:3001/api/user-transactions",
          {
            method: "GET", // Correct placement of the method
            headers: { Authorization: token },
          }
        );

        if (!response.ok) {
          throw new Error("Failed to fetch transactions");
        }

        const data = await response.json();
        setTransactions(data.transactions);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching user transactions:", err);
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const goToPaymentPage = () => {
    navigate("/pay");
  };

  return (
    <div className="container">
      <header className="dashboard-header">
        <h1 className="dashboard-title">User Dashboard</h1>
        <div className="dashboard-actions">
          <button className="button primary-button" onClick={handleLogout}>
            Logout
          </button>
          <button className="button secondary-button" onClick={goToPaymentPage}>
            Go to Pay
          </button>
        </div>
      </header>
      <section className="transactions-section">
        <h2>Your Transactions</h2>
        {loading ? (
          <p>Loading your transactions...</p>
        ) : transactions.length > 0 ? (
          <table className="transactions-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Amount</th>
                <th>Recipient</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction._id}>
                  <td>{new Date(transaction.createdAt).toLocaleString()}</td>
                  <td>{transaction.amount}</td>
                  <td>{transaction.recipientEmail}</td>
                  <td>{transaction.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No transactions found.</p>
        )}
      </section>
    </div>
  );
}

export default Dashboard;
