import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const [amount, setAmount] = useState("");
  const [recipient1, setRecipient1] = useState("");
  const [recipient2, setRecipient2] = useState("");
  const [paymentStatus, setPaymentStatus] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the logged-in user's email from the backend
    const fetchUserData = async () => {
      try {
        const response = await fetch("http://localhost:4001/api/user-info", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Ensure token is available in local storage
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch user info");
        }

        const userData = await response.json();
        setRecipient1(userData.email);
      } catch (error) {
        console.error("Error fetching user data:", error);
        navigate("/"); // Redirect to login or error page
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userEmail");
    navigate("/");
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    setPaymentStatus("Processing...");

    try {
      const response = await fetch("http://localhost:4001/api/make-payment", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Ensure token is included in header
        },
        body: JSON.stringify({
          amount,
          recipients: [recipient1, recipient2],
        }),
      });

      const data = await response.json();

      if (data.success) {
        setPaymentStatus("Payment Successful!");
      } else {
        setPaymentStatus("Payment Failed. " + data.error);
      }
    } catch (error) {
      setPaymentStatus("An error occurred. Please try again.");
    }
  };

  return (
    <div className="container">
      <div className="dashboard-header">
        <h1 className="title">User Dashboard</h1>
        <button className="button primary-button" onClick={handleLogout}>
          Logout
        </button>
        <h2 className="subtitle">Make a Payment</h2>
        <form onSubmit={handlePayment} className="form">
          <div className="form-group">
            <label>Amount (USD)</label>
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="input"
              required
            />
          </div>

          <div className="form-group">
            <label>Recipient 1 Email</label>
            <input type="email" value={recipient1} className="input" disabled />
          </div>

          <div className="form-group">
            <label>Recipient 2 Email</label>
            <input
              type="email"
              value={recipient2}
              onChange={(e) => setRecipient2(e.target.value)}
              className="input"
              required
            />
          </div>

          <div className="form-group">
            <button type="submit" className="btn btn-primary">
              Send Payment
            </button>
          </div>
        </form>

        {paymentStatus && <p>{paymentStatus}</p>}
      </div>
    </div>
  );
}

export default Dashboard;
